import { useState } from "react";

export default function NavBar() {
  const [bgColor, setBgColor] = useState("transparent");

  const updateBgColor = () => {
    if (window.scrollY < 618) {
      setBgColor("transparent");
    } else if (window.scrollY >= 618 && window.scrollY < 2256) {
      setBgColor("#b5d1cceb");
    } else if (window.scrollY >= 2256) {
      setBgColor("#fff59eeb");
    }
  };

  const colorStyle = {
    backgroundColor: bgColor,
  };

  window.addEventListener("scroll", updateBgColor);

  return (
    <div className="header" style={colorStyle}>
      <nav className="navbar">
        <h1 className="header-title">
          <a href="/" id="main-button">
            Film Diary
          </a>
        </h1>
        <ul className="navbar-menu">
          <li>
            <a href="#main-section">Watched</a>
          </li>
          <li>
            <a href="#wish-list">Wish List</a>
          </li>
          <li>
            <a href="#currently-list">Currently Showing</a>
          </li>
          <li>
            <a href="#upcoming-list">Upcoming</a>
          </li>
        </ul>
      </nav>
    </div>
  );
}
