import "./App.css";
import UpcomingList from "./components/UpcomingList";
import CurrentlyShowing from "./components/CurrentlyShowing";
import MainSection from "./components/MainSection";
import WishList from "./components/WishList";
import NavBar from "./components/NavBar";
import MovieOverlay from "./components/MovieOverlay";
import { useState } from "react";

import dummyData from "./dummyData";

function App() {
  const [overlayOpen, setOverlayOpen] = useState(false);

  function handleClick() {
    console.log(`Handle click before. Overlay open = ${overlayOpen}`);
    setOverlayOpen((prev) => !prev);
    console.log(`Handle click after. Overlay open = ${overlayOpen}`);
  }

  const closeOverlay = () => setOverlayOpen(false);

  return (
    <div className="App">
      {overlayOpen && (
        <MovieOverlay movieData={dummyData[1]} handleClose={closeOverlay} />
      )}
      <NavBar />
      <div>
        <h1 className="title" id="title">
          Film Diary
        </h1>
      </div>
      <MainSection handleClick={handleClick} />

      <WishList handleClick={handleClick} />
      <CurrentlyShowing handleClick={handleClick} />
      <UpcomingList handleClick={handleClick} />
    </div>
  );
}

export default App;
