import { useState } from "react";

export default function NavBar() {
  const [fullNavBarOn, setFullNavBarOn] = useState(false);
  const [bgColor, setBgColor] = useState("transparent");

  const showFullNavBar = () => {
    console.log(window.scrollY);
    if (window.scrollY >= 618) {
      setFullNavBarOn(true);
    } else {
      setFullNavBarOn(false);
    }
  };

  //   const updateBgColor = () => {
  //     if (window.scrollY >= 600 && window.scrollY <) {
  //         setBgColor("#b5d1cceb");
  //     } else {
  //         setBgColor("#b5d1cceb");
  //     }
  //   };

  window.addEventListener("scroll", showFullNavBar);

  return (
    <div className={fullNavBarOn ? "header header-bg" : "header"}>
      <nav className="navbar">
        <h1 className="header-title">Film Diary</h1>
        <ul className="navbar-menu">
          <li>Watched</li>
          <li>Wish List</li>
          <li>Currently Showing</li>
          <li>Upcoming</li>
        </ul>
      </nav>
    </div>
  );
}
