import { useState } from "react";

export default function NavBar() {
  const [bgColor, setBgColor] = useState("transparent");

  const updateBgColor = () => {
    if (window.scrollY < 618) {
      setBgColor("transparent");
    } else if (window.scrollY >= 618 && window.scrollY < 2256) {
      setBgColor("#b5d1cceb");
    } else if (window.scrollY >= 2256) {
      setBgColor("#fff59eeb");
    }
  };

  const colorStyle = {
    backgroundColor: bgColor,
  };

  window.addEventListener("scroll", updateBgColor);

  return (
    <div className="header" style={colorStyle}>
      <nav className="navbar">
        <h1 className="header-title">Film Diary</h1>
        <ul className="navbar-menu">
          <li>Watched</li>
          <li>Wish List</li>
          <li>Currently Showing</li>
          <li>Upcoming</li>
        </ul>
      </nav>
    </div>
  );
}
